mapper.select:
select id, create_time, pid, name
from t_module
where name = #{name}

select m.id, m.create_time, m.update_time, m.pid, m.name, m.latest_version, m.description, tp.name as projectName from t_module m left join t_project tp on m.pid = tp.id
where m.pid = #{pid}
order by tp.name ASC, m.name ASC
